import React, { Component } from "react";
import "../styles/SearchForInternshipsScreen.css";
import firebase from "../../firebase";
import BigCustomButton from "../../components/src/BigCustomButton";
import image from "../../media/accessdenied.jpeg";
import CustomButton from "../../components/src/CustomButton";
import { WaveLoading } from "react-loadingg";

class SearchForInternshipsScreen extends Component {
  constructor(props) {
    super(props);
    this.state = {
      uid: "",
      access: true,
      loading: true,
      roles: [<BigCustomButton value={"Test"}/>, <BigCustomButton value={"Test"}/>, <BigCustomButton value={"Test"}/>, <BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>,<BigCustomButton value={"Test"}/>]
    };
  }

  getUserID = () => {
    let authFlag = true;
    firebase.auth().onAuthStateChanged((user) => {
      if (authFlag) {
        authFlag = false;
        if (user) {
          this.setState({ uid: user.uid });
          this.setState({ access: true });
        } else {
          this.setState({ access: false });
        }
      }
    });
  };

  componentDidMount() {
    this.getUserID();
    this.id = setTimeout(() => this.setState({ loading: false }), 100);
  }

  componentWillUnmount() {
    clearTimeout(this.id);
  }

  render() {
    return this.state.access ? (
      this.state.loading ? (
        <WaveLoading />
      ) : (
        <div className="main-div">
          <div className="header-search">Pick an Industry and Then a Role</div>
          <div className="wrapper">
            <div className="flex-wrap">
              <CustomButton value={"Healthcare"}/>
              <CustomButton value={"Technology"}/>
              <CustomButton value={"Real Estate"}/>
              <CustomButton value={"Retail"}/>
            </div>
            <br /> <br />
            <div className="flex-wrap">
              <CustomButton value={"Education"}/>
              <CustomButton value={"Government"}/>
              <CustomButton value={"Finance"}/>
              <CustomButton value={"Business"}/>
            </div>
            <br /> <br />
          </div>
          <div>
            {this.state.roles}
          </div>
        </div>
      )
    ) : (
      <div className={"denied-wrapper"}>
        <img src={image} alt={"access denied"} style={{ height: "330px" }} />
        <br /> <br /> <br /> <br />
        <BigCustomButton
          value={"Click Here To Log In"}
          onClick={() => {
            window.location.href = "/landing";
          }}
        />
      </div>
    );
  }
}

export default SearchForInternshipsScreen;
